import React, { Component } from 'react'
import { Form, Button, Col, Container, Row, Card, Alert } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import firebase from '../configuration/configBookland'

const db = firebase.firestore();


class Addbook extends Component {
    state = {
        bookData: [],
        bookName: '',
        authorName: '',
        price: '',
        isbn: '',
        username: '',
        password: ''
    }

   
    componentDidMount() {
        db.collection('books').get().then(data => {
            data.docs.forEach(doc => {
                this.setState({
                    bookData: [...this.state.bookData, doc.data()]
                })

            })
        })
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleFormUsername = (e) => {
        this.setState({
            username: e.target.value
        })
    }

    handleFormPassword = (e) => {
        this.setState({
            password: e.target.value
        })
    }

    
    submitData = (e) => {
        e.prewentDefault();

        db.collection('books').add({
            bookName: this.state.bookName,
            authorName: this.state.authorName,
            price: this.state.price,
            isbn: this.state.isbn
        })

    const username = this.handleFormUsername.value;
    const password= this.handleFormPassword.value;

    if (username === "bookadmin" && password === "pwdadmin"){

    } else {
        [  'dark',
          ].map((variant, idx) => (
            <Alert key={idx} variant={variant}>
              Invalid username or password
            </Alert>
          ));
    }

    }

    render() {
        return (
            <div>
                <h1>AVAILABLE BOOKS</h1>
                <Card style={{ width: '18rem' }}>
                    <Card.Body>

                        {this.state.bookData.length ? (
                            this.state.bookData.map(item => {
                                return (
                                    <div>
                                        <p>Book Name: {item.bookName} </p>
                                        <p>Author: {item.authorName} </p>
                                        <p>Price: {item.price} </p>
                                        <p>ISBN: {item.isbn} </p>
                                    </div>
                                )
                            })
                        ) : (
                                <p>Loading</p>
                            )}
                    </Card.Body>
                </Card>

                <h1>ADD NEW BOOK</h1>
                <div>
                    <Container>
                        <Form onSubmit={this.submitData}>
                            <Row>
                                <Col>
                                    <Form.Group controlId="bookName">
                                        <Form.Label>BOOK NAME</Form.Label>
                                        <Form.Control type="bookName" placeholder="Enter Book Name"
                                            name="bookName"
                                            onChange={this.handleChange} />
                                        <Form.Text className="text-muted">
                                        </Form.Text>
                                    </Form.Group>
                                </Col>
                            </Row>

                            <Row>
                                <Col>
                                    <Form.Group controlId="author">
                                        <Form.Label>AUTHOR</Form.Label>
                                        <Form.Control type="authorName" placeholder="Author Name"
                                            name="authorName"
                                            onChange={this.handleChange} />
                                        <Form.Text className="text-muted">
                                        </Form.Text>
                                    </Form.Group>
                                </Col>
                            </Row>

                            <Row>
                                <Col>
                                    <Form.Group controlId="price">
                                        <Form.Label>PRICE</Form.Label>
                                        <Form.Control type="price" placeholder="Price"
                                            name="price"
                                            onChange={this.handleChange} />
                                        <Form.Text className="text-muted">
                                        </Form.Text>
                                    </Form.Group>
                                </Col>
                            </Row>

                            <Row>
                                <Col>
                                    <Form.Group controlId="isbn">
                                        <Form.Label>ISBN</Form.Label>
                                        <Form.Control type="isbn"
                                            name="isbn"
                                            onChange={this.handleChange} />
                                        <Form.Text className="text-muted">
                                        </Form.Text>
                                    </Form.Group>
                                </Col>
                            </Row>

                            <Form.Row className="align-items-center">
                                <Col xs="auto">
                                    <Form.Group controlId="username">
                                        <Form.Label>USERNAME</Form.Label>
                                        <Form.Control type="username" placeholder="Enter Username"
                                            name="username"
                                            onChange={this.handleFormUsername} />
                                        <Form.Text className="text-muted">
                                        </Form.Text>
                                    </Form.Group>
                                </Col>

                                <Col xs="auto">
                                    <Form.Group controlId="password">
                                        <Form.Label>PASSWORD</Form.Label>
                                        <Form.Control type="password" placeholder="Password"
                                            name="password"
                                            onChange={this.handleFormPassword} />
                                    </Form.Group>
                                </Col>
                            </Form.Row>

                            <Button style={{ background: "#D96704", color: "#FFFFFF" }} variant="primary" type="addbook" onClick={this.submitData}>
                                ADD BOOK
                    </Button>

                        </Form>
                    </Container>
                </div>
            </div>
        )
    }
}

export default Addbook;
