import React, { Component } from 'react'
import { Form, Button, Col, Container, Row } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import firebase from '../configuration/configBookland'

const db = firebase.firestore();

class Addbook extends Component {
    state={
        bookName:'',
        authorName:'',
        price:'',
        isbn:'',
        username:'',
        password:'',
        bookData: []
    }

    componentDidMount(){
        db.collection('books').get().then(data =>{
            data.docs.forEach(doc =>{
                this.setState({
                    bookData: [...this.state.bookData, doc.data()]
                })
                
            })  
        })
    }

    handleFormData= (e)=> {
this.setState({
    bookName:e.target.value
})
    }
    handleFormAuthor = (e) => {
        this.setState({
            authorName: e.target.value
        })
    }

    handleFormPrice = (e) => {
        this.setState({
            price: e.target.value
        })
    }

    handleFormIsbn = (e) => {
        this.setState({
            isbn: e.target.value
        })
    }

    handleFormUsername = (e) => {
        this.setState({
            username: e.target.value
        })
    }

    handleFormPassword = (e) => {
        this.setState({
            password: e.target.value
        })
    }

 addData = (e) => {
     e.preventDefault();
     console.log("Adding Data...");
     console.log(this.state.bookName);
     console.log(this.state.authorName);
     console.log(this.state.price);
     console.log(this.state.isbn);
     console.log(this.state.username);
     console.log(this.state.password);
 }
 

    render() {
        return (
            <div>
                <Card style={{ width: '18rem' }}>
                    <Card.Img variant="top" src="holder.js/100px180" />
                    <Card.Body>
                        <Card.Title>Card Title</Card.Title>
                        <Card.Text>
                            Some quick example text to build on the card title and make up the bulk of
                            the card's content.
    </Card.Text>
                        <Button variant="primary">Go somewhere</Button>
                    </Card.Body>
                </Card>

                <h1>ADD NEW BOOK</h1>
            
                <Container>
                    <Form>

                        <Row>
                            <Col>
                                <Form.Group controlId="bookName">
                                    <Form.Label>BOOK NAME</Form.Label>
                                    <Form.Control type="bookName" placeholder="Enter Book Name" 
                                    name="bookName"
                                    onChange={this.handleFormData} />
                                    <Form.Text className="text-muted">
                                    </Form.Text>
                                </Form.Group>
                            </Col>
                        </Row>

                        <Row>
                            <Col>
                                <Form.Group controlId="author">
                                    <Form.Label>AUTHOR</Form.Label>
                                    <Form.Control type="authorName" placeholder="Author Name"
                                        name="authorName"
                                        onChange={this.handleFormAuthor} />
                                    <Form.Text className="text-muted">
                                    </Form.Text>
                                </Form.Group>
                            </Col>
                        </Row>

                        <Row>
                            <Col>
                                <Form.Group controlId="price">
                                    <Form.Label>PRICE</Form.Label>
                                    <Form.Control type="price" placeholder="Price"
                                        name="price"
                                        onChange={this.handleFormPrice} />
                                    <Form.Text className="text-muted">
                                    </Form.Text>
                                </Form.Group>
                            </Col>
                        </Row>

                        <Row>
                            <Col>
                                <Form.Group controlId="isbn">
                                    <Form.Label>ISBN</Form.Label>
                                    <Form.Control type="isbn"
                                        name="isbn"
                                        onChange={this.handleFormIsbn} />
                                    <Form.Text className="text-muted">
                                    </Form.Text>
                                </Form.Group>
                            </Col>
                        </Row>

                        <Row>
                        <Col>
                        <Form.Group>
                            <Form.File id="bookImage" label="BOOK IMAGE INPUT" />
                        </Form.Group>
                       </Col>
                       </Row>
                       
                        <Form.Row className="align-items-center">
                            <Col xs="auto">
                                <Form.Group controlId="username">
                                    <Form.Label>USERNAME</Form.Label>
                                    <Form.Control type="username" placeholder="Enter Username" 
                                    name="username"
                                    onChange={this.handleFormUsername}/>
                                    <Form.Text className="text-muted">
                                    </Form.Text>
                                </Form.Group>
                            </Col>

                            <Col xs="auto">
                                <Form.Group controlId="password">
                                    <Form.Label>PASSWORD</Form.Label>
                                    <Form.Control type="password" placeholder="Password" 
                                    name="password"
                                    onChange={this.handleFormPassword}/>
                                </Form.Group>
                            </Col>
                        </Form.Row>

                
                        <Button style={{ background: "#D96704", color: "#FFFFFF" }} variant="primary" type="addbook" onClick={this.addData}>
                            ADD BOOK
                    </Button>

                    </Form>
                </Container>
            </div>
        )
    }
}

export default Addbook;
